SELECT r.entity_pk_value as `Product ID`,pro.value as `Product Name` , rd.title , rd.detail, rd.nickname, r.created_at, rating.value as `rating` FROM `review` as `r` CROSS JOIN rating_option_vote as rating CROSS JOIN review_detail AS rd CROSS JOIN catalog_product_entity_varchar as pro WHERE pro.attribute_id = 71 AND pro.store_id = 0 AND pro.entity_id =r.entity_pk_value AND r.review_id = rd.review_id AND r.entity_id = 1 AND rating.review_id = r.review_id ORDER BY `r`.`entity_pk_value` ASC;
